var RMCModel = function() {}

RMCModel.prototype.doRequest = function(methodName, methodParams, callback){
    var data = {
        modelName : this.modelName,
        calledMethod : methodName,
        methodProperties: methodParams
    }
    if(callback === undefined){
        return $.ajax({
            url: 'http://127.0.0.1/RMCFramework/index.php?action=RemoteModelCall',
            type: 'POST',
            data: JSON.stringify(data),
            async: false,
            contentType: 'application/json; charset=utf-8'
        }).responseText;
    } else {
        $.ajax({
            url: 'http://127.0.0.1/RMCFramework/index.php?action=RemoteModelCall',
            type: 'POST',
            data: JSON.stringify(data),
            contentType: 'application/json; charset=utf-8',
            success: callback
        })
    }

}

